import React from 'react';

const DynamicPlan = () => {
  // Note: Interactive logic for the toggle switches will be added later with useState
  return (
    <main className="flex-1 px-4 sm:px-10 lg:px-20 py-16 lg:py-24">
      <div className="max-w-6xl mx-auto">
        <div className="text-center">
          <h1 className="text-4xl md:text-5xl font-extrabold tracking-tighter leading-tight mb-6 text-[var(--text-primary-color)] dark:text-white">
            Dinamik İttifak Müzakeresi
          </h1>
          <p className="text-lg md:text-xl text-[var(--text-secondary-color)] max-w-3xl mx-auto">
            Bu bir fiyatlandırma sayfası değildir. Bu, ortaklığımızın şartlarını birlikte belirleyeceğimiz dijital müzakere masasıdır. Sorumlulukları seçin, kâr paylaşım oranının anında nasıl değiştiğini izleyin ve size özel ittifakı yaratın.
          </p>
        </div>
        {/* Profit Share Display */}
        <div id="share-display" className="sticky top-4 z-20 mt-16 bg-[var(--background-secondary-color)] dark:bg-black/50 backdrop-blur-lg border border-[var(--divider-color)] dark:border-white/10 rounded-xl shadow-2xl p-6">
          <h2 className="text-center text-2xl font-bold text-[var(--text-primary-color)] mb-4">Anlık Kâr Paylaşımı Oranı</h2>
          <div className="flex justify-center items-center gap-8">
            <div className="text-center">
              <p className="text-lg font-medium text-[var(--text-secondary-color)]">Sizin Payınız</p>
              <p id="seller-share" className="text-5xl font-bold text-white transition-colors duration-300">50%</p>
            </div>
            <div className="text-5xl font-bold text-[var(--text-secondary-color)]">/</div>
            <div className="text-center">
              <p className="text-lg font-medium text-[var(--text-secondary-color)]">Ekobol'ün Payı</p>
              <p id="ekobol-share" className="text-5xl font-bold text-[var(--primary-color)] transition-colors duration-300">50%</p>
            </div>
          </div>
        </div>
        {/* Form and other sections would continue here */}
      </div>
    </main>
  );
};

export default DynamicPlan;
