import React, { useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import Chart from 'chart.js/auto';

const LiveAnalytics = () => {
  const chartRef = useRef(null);
  const chartInstance = useRef(null);

  useEffect(() => {
    // Function to create Live Sales Chart
    const createLiveSalesChart = () => {
      if (chartInstance.current) {
        chartInstance.current.destroy();
      }
      const ctx = chartRef.current.getContext('2d');

      const labels = [];
      for (let i = 59; i >= 0; i--) {
          labels.push(`${i} dk önce`);
      }
      labels[59] = 'Şimdi';

      const salesData = Array(60).fill(0).map((_, i) => (Math.random() * 20 + (i / 10)).toFixed(2));
      salesData[59] = (Math.random() * 50).toFixed(2);

      chartInstance.current = new Chart(ctx, {
          type: 'line',
          data: {
              labels: labels,
              datasets: [{
                  label: 'Canlı Satışlar (₺)',
                  data: salesData,
                  borderColor: 'rgb(14, 205, 144)',
                  backgroundColor: 'rgba(14, 205, 144, 0.2)',
                  fill: true,
                  tension: 0.2,
                  pointRadius: 0,
                  pointHitRadius: 10,
                  pointBorderColor: 'transparent',
                  pointBackgroundColor: 'transparent',
              }]
          },
          options: {
              responsive: true,
              maintainAspectRatio: false,
              plugins: {
                  legend: { display: false },
                  tooltip: { mode: 'index', intersect: false }
              },
              scales: {
                  x: {
                      display: true,
                      grid: { display: false },
                      ticks: {
                          autoSkip: true,
                          maxTicksLimit: 10
                      }
                  },
                  y: {
                      display: true,
                      beginAtZero: true,
                      grid: { color: 'rgba(0,0,0,0.1)' },
                      ticks: {
                          callback: function(value) {
                              return '₺' + value;
                          }
                      }
                  }
              },
              animation: false
          }
      });
    };

    createLiveSalesChart();

    const interval = setInterval(() => {
      document.getElementById('live-visitors').textContent = Math.floor(Math.random() * 20) + 5;
      document.getElementById('live-sales').textContent = '₺' + (Math.random() * 300).toFixed(2);
    }, 5000);

    return () => {
      clearInterval(interval);
      if (chartInstance.current) {
        chartInstance.current.destroy();
      }
    };
  }, []);

  return (
    <main className="flex flex-1 justify-center py-10 bg-[var(--background-secondary-color)]">
        <div className="layout-content-container flex flex-col w-full max-w-6xl px-4">
            <div className="mb-8">
                <h1 className="text-[var(--text-primary-color)] text-4xl font-bold leading-tight tracking-tight">Canlı Analitik</h1>
                <p className="text-[var(--text-secondary-color)] text-base font-normal leading-normal mt-1">Gerçek zamanlı satış verilerini ve site etkinliğini izleyin.</p>
            </div>
            <div className="border-b border-[var(--divider-color)] mb-8">
                <nav className="flex gap-8 -mb-px">
                    <Link className="flex items-center justify-center py-4 px-1 inactive-tab" to="/dashboard">
                        <span className="text-sm font-semibold">Genel Bakış</span>
                    </Link>
                    <Link className="flex items-center justify-center py-4 px-1 inactive-tab" to="/trends">
                        <span className="text-sm font-semibold">Eğilimler</span>
                    </Link>
                    <Link className="flex items-center justify-center py-4 px-1 active-tab" to="/live-analytics">
                        <span className="text-sm font-semibold">Canlı</span>
                    </Link>
                </nav>
            </div>

            <section className="mb-12 grid grid-cols-1 md:grid-cols-3 gap-6">
                <div className="bg-white rounded-xl border border-[var(--divider-color)] shadow-sm p-6">
                    <p className="text-lg font-medium text-[var(--text-secondary-color)]">Şu Anki Ziyaretçiler</p>
                    <p id="live-visitors" className="text-4xl font-bold text-[var(--primary-color)] mt-2">12</p>
                    <p className="text-sm text-[var(--text-secondary-color)] mt-1">Son 5 dakikadaki aktif kullanıcılar</p>
